                  {
                    "linkId": "394",
                    "answer": [
                      {"valueString": "Risikoklasse I nach MDR"}
                    ]
                  },
                  {
                    "linkId": "402",
                    "answer": [
                      {
                        "valueAttachment": {
                          "contentType": "application/pdf",
                          "url": "https://diga-api.bfarm.de/diga-vz/media/1906",
                          "title":
                              "Instructions for Use_vorvida_DE_EU_v03_uhu0nhksyhd58w"
